    private static final int PLATFORM;

    static {
        String osName = getPrivilegedProperty("os.name");
        if (osName.startsWith("Windows")) {
            PLATFORM = 3;
        } else if (osName.startsWith("Linux") || osName.startsWith("FreeBSD") || osName.startsWith("OpenBSD") || osName.startsWith("SunOS") || osName.startsWith("Unix")) {
            PLATFORM = 1;
        } else if (osName.startsWith("Mac OS X") || osName.startsWith("Darwin")) {
            PLATFORM = 2;
        } else {
            throw new LinkageError("Unknown platform: " + osName);
        }
    }

    /**
     * Loads the specified icon from the specified string.
     *
     * @param icon The icon string.
     * @return The icon as a {@link ByteBuffer}.
     *
     * @apiNote Icon stored in string as byte-equivalent characters.
     */
    public static ByteBuffer loadIcon(String icon) {
        try {
            ByteBuffer buffer = BufferUtils.createByteBuffer(icon.length());
            for (int i = 0; i < icon.length(); i++) {
                buffer.put((byte) icon.charAt(i));
            }
            buffer.flip();
            return buffer;
        } catch (Exception e) {
            throw new RuntimeException(e);
        }
    }

